import React from "react";
import PDFViewer from './PDFViewer';
import PDFJSBackend from '../../backends/pdfjs';
import {withRouter} from 'react-router-dom';
import queryString from 'query-string'
import classes from './PDFViewerBase.module.css';



class PDFViewerBase extends React.Component {

    UNSAFE_componentWillMount (){
      this.pdfSource = queryString.parse(this.props.location.search).q;
    }

    render() {
      return (
        <div className={classes.Body}>
        <iframe
        src={`/pdfjs-2.2.228-dist/web/viewer.html?file=${this.pdfSource}`}
        width="100%"
        height="100%"
        >
        </iframe>
        </div>
      );
    }
  }

export default withRouter(PDFViewerBase);
