import React, {Component} from 'react';
import classes from './ViewProjectLink.module.css'
import {Link, withRouter} from 'react-router-dom';
// import PDFViewer from '../../components/PDFViewer/PDFViewer';

class ViewProjectLink extends Component {
  state ={
    titleHover: false,
    fileIsPDF: false
  }

  onHover = (e) => {
    this.setState({ titleHover: true });
  }

  componentDidMount(){
    this.linkSrc = this.props.pdfSrc
    if (this.linkSrc.includes('pdf')) {
      this.setState({ fileIsPDF: true });
    }
  }

  render(){
    let typeOfLink;
    let cardContentDiv = (
        <div
          onMouseOver={this.onHover.bind(this)}
          onMouseLeave={() => this.setState({titleHover: false})}
          className={classes.FullOverlay}>
          <img src={this.props.cardImage} alt="?"></img>
        { this.state.titleHover ?
          <div
            className={classes.Viewproject}>
            <h3>View Project</h3>
        </div> : true }
      </div>
    )

    if (this.state.fileIsPDF) {
      typeOfLink =  (
          <Link
            to={{
              pathname: `/view-pdf/`,
              search: "?q=" + this.linkSrc
            }}
            target="_blank"
            >
            {cardContentDiv}
        </Link>)
    }else {
      typeOfLink = (
        <a
          href={this.linkSrc}
          target="_blank"
          >
          {cardContentDiv}
        </a> )
    }

    return(
      <div className={classes.Projectdesc}>
        {typeOfLink}
      </div>
    )
  }
}

export default withRouter(ViewProjectLink);
