import React, {Component, useState} from 'react';
import classes from './CenterCard.module.css';
import Aux from '../../../../hoc/Aux/Aux'
import { Container, Row, Col } from 'react-bootstrap';


class centercard extends Component {

  state ={
    setIsShown: false
  }

  showHandler = (e) =>{
    console.log('show stuff');
  }

  render(){

    return(
      <Row className={classes.Projectcard}>
        <Col className={"md-6 " + ' ' + classes.Projectheader}>
          <div className={classes.Projectdesc}>
            <a href="#" target="_blank">
              <div
                onMouseOver={() => this.showHandler()}
                className={classes.FullOverlay}>
                <img src={this.props.imageleft} alt="image"></img>
                <div
                  className={classes.Viewproject}
                  onMouseLeave={() => this.setState({setIsShown: false})}>
                  <h3>View Project</h3>
                </div>
              </div>
            </a>
            <br>
            </br>
            <p>
              Audience: Georgetown ISD Teachers
              <br>
              </br>
              Date: Spring 2019
              <br>
              </br>
              Role: Instructional Designer & Trainer
              <br>
              </br>
              Medium: Instructor-led course with workbook Tools: Adobe Indesign, Google Classroom, Nearpod
            </p>
          </div>
        </Col>
        <Col className="md-6">
          <img src={this.props.imageright} alt="image"></img>
        </Col>
      </Row>
    )
  }
}

export default centercard;
